import NetworkKit
import XCTest

final class CertificateManagerTests: XCTestCase {
    func testCertificateManagerInitialization() {
        // Given
        let mockPEMKeys: [[Int]] = [
            mockPEMKeys, mockPEMKeys
        ]

        // When
        let certificateManager = CertificateManager(keys: mockPEMKeys)

        // Then
        XCTAssertEqual(certificateManager.publicKeys.count, mockPEMKeys.count, "The number of public keys should match the number of PEM inputs.")
        for publicKey in certificateManager.publicKeys {
            XCTAssertNotNil(publicKey, "Public keys should not be nil.")
        }
    }

    func testCertificateManagerEmptyKeys() {
        // Given
        let mockPEMKeys: [[Int]] = [] // Empty input

        // When
        let certificateManager = CertificateManager(keys: mockPEMKeys)

        // Then
        XCTAssertEqual(certificateManager.publicKeys.count, 0, "The publicKeys array should be empty when initialized with no PEM inputs.")
    }

